package com.spring.source.code.dao;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.jdbc.core.JdbcTemplate;import org.springframework.stereotype.Repository;/** * @author * @description * @date 2020/1/9 */@Repositorypublic class LogDao {    @Autowired    private JdbcTemplate jdbcTemplate;    public void add(String msg) {        String sql = "INSERT INTO t_log(msg) VALUES(?);";        int updateResult = jdbcTemplate.update(sql, msg);        System.out.println("updateResult:" + updateResult);    }}