package com.spring.source.code.main;import com.spring.source.code.service.UserService;import org.springframework.context.support.ClassPathXmlApplicationContext;/** * @author * @description * @date 2020/1/8 */public class TestMain {    public static void main(String[] args) {        ClassPathXmlApplicationContext classPathXmlApplicationContext = new ClassPathXmlApplicationContext("spring.xml");        UserService userService = (UserService) classPathXmlApplicationContext.getBean("userServiceImpl");        try {            userService.add();        } catch (Exception e) {            e.printStackTrace();        }        try {            userService.del();        } catch (Exception e) {            e.printStackTrace();        }    }}